movement.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    var result = [];
    values.forEach(function (value) {
      value.forEach(function (point) {
          result.push(point);
        }
      )
    });

    var uniquePoints = [];
    result.forEach(function (point) {
        var existing = false;
        for (var i = 0; i < uniquePoints.length; i++) {
            if (uniquePoints[i][0] === point[0] && uniquePoints[i][1] === point[1]) {
                existing = true;
                break;
            }
        }

        if (existing === false) {
            uniquePoints.push(point);
        }
    });

    if (uniquePoints.length > 1) {
      return uniquePoints;
    } else {
      return [];
    }
  } else {
    return values;
  }
}

find-route.map:
function (doc) {
  if (doc.raw_data && doc.calculated_coordinates.length === 2) {
    var raw_data = doc.raw_data
    emit([raw_data.user.id, raw_data.user.screen_name], [[doc.calculated_coordinates[1], doc.calculated_coordinates[0]], doc.created_at], 1);
  }
}

find-route.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    return sum(values);
  } else {
    return values.length;
  }
}
