total-tweets-by-day-n-hour.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    return sum(values);
  } else {
    return values.length;
  }
}

total-tweets-by-day-n-hour.map:
function (doc) {
  if (doc.raw_data) {
    var data = doc.raw_data;
    var date = new Date(data.created_at)
    var day = date.getDay();
    var hour = date.getHours()

    emit([day, hour], 1);
  }
}

tweets-by-categories.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    return sum(values);
  } else {
    return values.length;
  }
}

tweets-by-categories.map:
function (doc) {
  if (doc.match_track_filter === true) {
    emit("Covid19", 1)
  } else {
    emit("Others", 1)
  }
}

tweets-with-coordinates.reduce:
function (keys, values, rereduce) {
  if (rereduce) {
    return sum(values);
  } else {
    return values.length;
  }
}

tweets-with-coordinates.map:
function (doc) {
  if (doc.calculated_coordinates) {
    if (doc.calculated_coordinates.length == 2) {
      if (doc.match_track_filter) {
        emit(["With Coordinates", "covid"], 1)
      } else {
        emit(["With Coordinates", "basic"], 1)
      }
    } else {
      if (doc.match_track_filter) {
        emit(["Without Coordinates", "covid"], 1)
      } else {
        emit(["Without Coordinates", "basic"], 1)
      }
    }
  }
}

